jQuery(function($){$.getJSON(umwActAlerts.ajaxurl,{'action':'check_active_alert','v':new Date().getTime()},function(result){if('emergency'in result){insert_emergency_alert(result.emergency)}if('alert'in result){insert_active_alert(result.alert)}});function insert_emergency_alert(data){if(typeof(data.html)==='undefined'||-1==data.html||'0'==data.html||null==data.html){return}var $umwh=$(data.html);if($('.home-top-left').length>0){$umwh.hide().prependTo($('.home-top-left')).fadeIn(1000)}else{if($('#wrap').length>0){$umwh.hide().prependTo($('#wrap')).fadeIn(1000);$('body').addClass('has-active-alert')}else if($('body').hasClass('wptouch-pro')){$umwh.hide().insertAfter($('#header')).fadeIn(1000);$('body').addClass('has-active-alert')}}}function insert_active_alert(data){if(typeof(data.html)==='undefined'||-1==data.html||'0'==data.html||null==data.html){return}if($('.home-top-left #eps-slideshow-caption').length<=0){return}var $umwah=$(data.html);$umwah.hide().appendTo($('.home-top-left')).fadeIn(1000)}});